:0;s:18:\"background_display\";s:4:\"tile\";}}s:22:\"so_sidebar_emulator_id\";s:34:\"siteorigin-panels-builder-24910000\";s:11:\"option_name\";s:32:\"widget_siteorigin-panels-builder\";}}s:5:\"grids\";a:1:{i:0;a:2:{s:5:\"cells\";i:1;s:5:\"style\";a:0:{}}}s:10:\"grid_cells\";a:1:{i:0;a:4:{s:4:\"grid\";i:0;s:5:\"index\";i:0;s:6:\"weight\";i:1;s:5:\"style\";a:0:{}}}}'),
